import React, {Component} from 'react';
import {Button} from 'react-bootstrap';
import axios, {post} from 'axios';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import TextField from '@material-ui/core/TextField';

const styles = theme => ({
    hidden: {
      display: 'none'
    }
});


class SendMessage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            file: null,
            userName: '',
            birthday: '',
            gender: '',
            job: '',
            fileName: '',
            open: false
        }
        /* this.handleFormSubmit = this.handleFormSubmit.bind(this)
        this.handleFileChange = this.handleFileChange.bind(this)
        this.handleValueChange = this.handleValueChange.bind(this)
        this.addCustomer = this.addCustomer.bind(this)
        this.handleClickOpen = this.handleClickOpen.bind(this)
        this.handleClose = this.handleClose.bind(this); */
    }
    handleFormSubmit(e) {
        e.preventDefault()
        this.addCustomer()
        .then((response) => {
            console.log(response.data);
            this.props.stateRefresh();
        })
        this.setState({
            file: null,
            userName: '',
            birthday: '',
            gender: '',
            job: '',
            fileName: '',
            open: false
        })
    }
    handleValueChange(e) {
        let nextState = {};
        nextState[e.target.name] = e.target.value;
        this.setState(nextState);
    }
    addCustomer(){
        const url = '/api/customers';
        const formData = new FormData();
        formData.append('image', this.state.file)
        formData.append('name', this.state.userName)
        formData.append('birthday', this.state.birthday)
        formData.append('gender', this.state.gender)
        formData.append('job', this.state.job)
        const config = {
            headers: {
            'content-type': 'multipart/form-data'
            }
        }
        return post(url, formData, config)
    }

    handleClickOpen() {
        this.setState({
            open: true
        });
    }
    handleClose() {
        this.setState({
            file: null,
            userName: '',
            birthday: '',
            gender: '',
            job: '',
            fileName: '',
            open: false
        })
    }
    sendMessage= async()=>{
        // alert(this.props.user.nickname);
        const user_id=this.props.user.id;
        const send_param={user_id};
        try{
            const result= await axios.post('http://localhost:9090/message/send', send_param);
            if(result.data.message){
                alert('쪽지보내기 성공');
            }else{
                alert('쪽지보내기 실패');
            }
        }catch(err){
            console.log(err);
        }
    }
    render(){
        const { classes } = this.props;
        return(
            <div>
                <Button variant="info" size="sm" onClick={this.handleClickOpen}>쪽지보내기</Button>
                <Dialog open={this.state.open} onClose={this.handleClose.bind(null)}>
                    <DialogTitle>고객 추가</DialogTitle>
                    <DialogContent>
                        <TextField label="이름" type="text" name="userName" value={this.state.userName} onChange={this.handleValueChange.bind(null)} /><br/>
                    </DialogContent>
                </Dialog>
            </div>



        );
    }
}

export default SendMessage;
