import React, {Component} from "react";
import axios from "axios";
import Button from 'react-bootstrap/Button';
import $ from 'jquery';
import './css/contact.css';
axios.defaults.withCredentials = true;
const headers = {withCredentials: true};

class Contact extends Component {
    state={
        nick: ""
    }
    signIn=()=>{
        const send_param = {
            nick: this.nickE.value,
            email: this.emailE.value,
            company: this.companyE.value,
            job: this.jobE.value
        }
        $.post('http://localhost:9090/member/insert', send_param, (returnData)=>{
            if(returnData.result){
                this.setState({
                    nick: returnData.result.nickname
                });
                alert(this.state.nick + "님, 회원가입 되셨습니다.");
            }else{
                alert('회원가입 거부');
            }
        });

    }
    render(){
        return(
            <div>
                <form>
                    <div>
                        <input type="text" placeholder="Nickname" ref={ref=>this.nickE=ref} required/><br/>
                        <input type="text" placeholder="E-mail" ref={ref=>this.emailE=ref} required/><br/>
                        <input type="text" placeholder="Company" ref={ref=>this.companyE=ref} required/><br/>
                        <input type="text" placeholder="Job" ref={ref=>this.jobE=ref}/><br/>
                    </div>
                    <Button type="submit" variant="success" onClick={this.signIn}>가입하기</Button>
                </form>
                <div class="container">
                    <Button variant="warning" class="cancelbtn">회원정보 찾기</Button>
                    <span class="psw">Forgot <a href="#">password?</a></span>
                </div>
            </div>
        );
    }
}

export default Contact;
