import React, {Component} from 'react';
import './css/main.css';
import $ from 'jquery';

class Board extends Component {
    inputFocus=()=>{
        const content = this.content.value;
        const sendParam = {
            content
        }
        $.post('http://localhost:8080/post/insert', sendParam, function(returnData){
            if(returnData){
                alert(returnData.message);
            }
        });
    }
    render(){
        return(
            <div>
                <div class="textbox">
                <label for="ex_input">닉네임</label> <input type="text" id="ex_input" ref={ref=>this.content=ref}/><br/>
                <label for="ex_input">이메일</label> <input type="text" id="ex_input" ref={ref=>this.content=ref}/><br/>
                <label for="ex_input">회사</label> <input type="text" id="ex_input" ref={ref=>this.content=ref}/><br/>
                <label for="ex_input">직무</label> <input type="text" id="ex_input" ref={ref=>this.content=ref}/><br/>
                <button>취소</button><button onClick={this.inputFocus}>등록</button>
                </div>
            </div>
        );
    }
}

export default Board;
