import React, {Component} from 'react';
import axios from 'axios';
import $ from 'jquery';
import MyPostItems from './mypostItems';
import {Col} from 'react-bootstrap';

axios.defaults.withCredentials = true;
const headers = {withCredentials: true};

class MyPost extends Component {
    constructor(props){
        super(props);
        this.state={
            myposts: []
        };
    }
    componentDidMount(){
        this.ShowPosts();
    }
    ShowPosts=async ()=>{
        const user_id=$.cookie("login_id");
        console.log(user_id);
        const send_param={user_id, headers};
        try{
            const result = await axios.post('http://localhost:9090/post/allpost_mine', send_param);
            if(result.data.myposts){
                this.setState({
                    myposts: result.data.myposts
                });
            }
            console.log(result.data);
        }catch(err){
            console.log(err);
        }
    }
    update_post=(post_id)=>{
        const user_id=$.cookie("login_id");
        const send_param={post_id, user_id};
        console.log(send_param);
        $.post('http://localhost:9090/post/update', send_param, (returnData)=>{
            if(returnData.result){
                alert("Post 수정완료");
            }else{
                alert("수정 오류");
            }
        });
    }
    delete_post=(post_id)=>{
        const user_id=$.cookie("login_id");
        const send_param={post_id, user_id};
        console.log(send_param);
        $.post('http://localhost:9090/post/delete', send_param, (returnData)=>{
            if(returnData.result){
                alert("Post 삭제완료");
                const filteredItems = this.state.posts.filter((item)=>{
                    return item.key !== post_id
                });
                this.setState = filteredItems;
            }else{
                alert("삭제 오류");
            }
        });
    }
    render(){
        return(
            <div>
                <Col md={{ span: 6, offset: 3 }}>
                <MyPostItems entries={this.state.myposts} superDelete={this.delete_post} superUpdate={this.update_post}/>
                </Col>
            </div>
        );
    }
}

export default MyPost;
