import React, {Component} from 'react';
import './css/main.css';
import $ from 'jquery';

class Board extends Component {
    inputFocus=()=>{
        const content = this.content.value;
        alert(content);
    }
    render(){
        return(
            <div>
                <div class="textbox">
                <label for="ex_input">내용 입력</label> <input type="text" id="ex_input" ref={ref=>this.content=ref}/><br/>
                <button>취소</button><button onClick={this.inputFocus}>등록</button>
                </div>
            </div>
        );
    }
}

export default Board;
