import React, {Component} from 'react';
import OpenMenu from './openMenu';
import Main from './main';
import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
import "./css/title.css";

class Root extends Component {
    /* constructor(props){
        super(props);
        this.state={
            visible: false
        };
        this.handleMouseDown = this.handleMouseDown.bind(this);
        this.toggleMenu = this.toggleMenu.bind(this);
    } */
    /* handleMouseDown(e){
        this.toggleMenu();

        console.log("clicked");
        e.stopPropagation();
    }
    toggleMenu(){
        this.setState({
            visible: !this.state.visible
        });
    } */
    openNav=()=>{
        document.getElementById('mysidenav').style.width = '250px';
    }
    closeNav=()=>{
        document.getElementById('mysidenav').style.width = '0';
    }
    render(){
        return(
            <div>
                {/* 메뉴 */}
                <nav>
                    {/* 숨김 메뉴 */}
                    {/* <OpenMenu handleMouseDown={this.handleMouseDown} menuVisibility={this.state.visible}/> */}
                    <div>
                    <div id="mysidenav" className="sidenav">
                        <ul>
                            <Button className="closebtn" variant="dark" onClick={this.closeNav}>Close</Button>
                            <li>잔소리하기</li>
                            <li>토픽</li>
                            <li>Contact</li>
                        </ul>
                    </div>
                        <span className="openmenu" onClick={this.openNav}>
                            <Button variant="outline-danger">Open</Button>
                        </span>
                    </div>

                    {{/* 타이틀 */}}
                    <div>
                        <section className="section intro">
                        <div className="container">
                        <h1>Blind</h1>
                        </div>
                        </section>
                    </div>
                </nav>
                <Main />    {{/* 본문 */}}
            </div>
        );
    }
}   

export default Root;
