import React, {Component} from 'react';
import {InputGroup, FormControl, Button} from 'react-bootstrap';

class Comments extends Component {
    comment_insert=()=>{
        alert(this.commentE.value);
    }
    render(){
        return(
            <div>
                <InputGroup size="sm" className="mb-3">
                    <InputGroup.Prepend>
                        <InputGroup.Text onClick={this.comment_insert}>댓글</InputGroup.Text>
                    </InputGroup.Prepend>
                    <FormControl ref={ref=>this.commentE=ref} aria-label="Small" aria-describedby="inputGroup-sizing-sm" />
                    <Button  variant="link">댓글 등록</Button>
                </InputGroup>
            </div>
        );
    }
}

export default Comments;
