import React, {Component} from 'react';
import axios from "axios";
import $ from 'jquery';
import PostItems from './postItems';
import {Button, Form} from 'react-bootstrap';

axios.defaults.withCredentials = true;
const headers = {withCredentials: true};

class ShowPost extends Component {
    constructor(props){
        super(props);
        this.state={
            posts: []
        };
    }
    componentDidMount(){
        this.ShowPosts();
    }
    ShowPosts=async ()=>{
        try{
            const result = await axios.post('http://localhost:9090/post/allpost', headers);
            if(result.data.posts){
                this.setState({
                    posts: result.data.posts
                });
            }
            console.log(result);
        }catch(err){
            console.log(err);
        }
    }
    delete_post=(post_id)=>{
        const send_param={post_id};
        console.log(send_param);
        $.post('http://localhost:9090/post/delete', send_param, (returnData)=>{
            if(returnData.result){
                alert("일정 삭제 성공");
                const filteredItems = this.state.posts.filter((item)=>{
                    return item.key !== key
                });
                this.setState = filteredItems;
            }else{
                alert("일정 삭제 오류");
            }
        });
    }
    upload_post = async()=>{
        const send_param ={
            headers,
            content: this.postContent.value,
            memberId: $.cookie("login_id")
        }
        try{
            await axios.post('http://localhost:9090/post/upload', send_param)
            .then((returnData)=>{
                if(returnData.data.message){
                    alert("게시물 등록 성공");
                }else{
                    alert("게시물 등록 실패");
                }
            });
        }catch(err){
            console.log(err);
        }
    }     
    render(){
        return(
            <div>
                <div>
                    <div className="comment_form">
                        <h2>여러분의 소식을 들려주세요</h2>
                        <Form>
                            <Form.Group controlId="exampleForm.ControlTextarea1">
                                <Form.Control as="textarea" rows="3" placeholder="입력하세요" ref={ref=>this.postContent=ref}/>
                            </Form.Group>
                        <Button onClick={this.upload_post}>글 등록</Button>
                        </Form>
                    </div>
                    <div id="post"></div>
                </div>
                <PostItems entries={this.state.posts} superDelete={this.delete_post} />
            </div>
        );
    }
}

export default ShowPost;
