import React, {Component} from 'react';
import "./css/menu.css";
import 'bootstrap/dist/css/bootstrap.min.css';

class openMenu extends Component {
    openNav=()=>{
        document.getElementById('mysidenav').style.width = '250px';
    }
    closeNav=()=>{
        document.getElementById('mysidenav').style.width = '0';
    }
    render(){
        return(
            <div>
                <div id="mysidenav" class="sidenav">
                    <li class="closebtn" onClick={this.closeNav}>X</li>
                    <li>잔소리하기</li>
                    <li>토픽</li>
                    <li>Contact</li>
                </div>
                <span class="openmenu" onClick={this.openNav}>
                    <i class="fa fa-angle-double-left fa-5" aria-hidden="true"></i>
                    <ButtonToolbar>
                        <Button type="button" variant="outline-danger">Open</Button>
                    </ButtonToolbar>
                </span>
            </div>
        );
    }
}

export default openMenu;
