import React, {Component} from 'react';
import axios from "axios";
import $ from 'jquery';
import PostItems from './postItems';
import {Button, Form} from 'react-bootstrap';

axios.defaults.withCredentials = true;
const headers = {withCredentials: true};

class ShowPost extends Component {
    constructor(props){
        super(props);
        this.state={
            posts: []
        };
    }
    componentDidMount(){
        this.ShowPosts();
    }
    ShowPosts=async ()=>{
        try{
            const result = await axios.post('http://localhost:9090/post/allpost', headers);
            if(result.data.posts){
                this.setState({
                    posts: result.data.posts
                });
            }
            console.log(result);
        }catch(err){
            console.log(err);
        }
    }
    update_post=(post_id)=>{
        const user_id=$.cookie("login_id");
        const send_param={post_id, user_id};
        console.log(send_param);
        $.post('http://localhost:9090/post/update', send_param, (returnData)=>{
            if(returnData.result){
                alert("Post 수정완료");
            }else{
                alert("수정 오류");
            }
        });
    }
    delete_post=(post_id)=>{
        const user_id=$.cookie("login_id");
        const send_param={post_id, user_id};
        console.log(send_param);
        $.post('http://localhost:9090/post/delete', send_param, (returnData)=>{
            if(returnData.result){
                alert("Post 삭제완료");
                const filteredItems = this.state.posts.filter((item)=>{
                    return item.key !== post_id
                });
                this.setState = filteredItems;
            }else{
                alert("삭제 오류");
            }
        });
    }
    
    render(){
        const PostStyle={
            width: "300"
        }
        return(
            <div>
                <div style={PostStyle}>
                    <PostItems entries={this.state.posts} superDelete={this.delete_post} superUpdate={this.update_post}/>   
                </div>
            </div>
        );
    }
}

export default ShowPost;
