import React, {Component} from 'react';
import {Button, Badge} from 'react-bootstrap';
import axios from 'axios';
import $ from 'jquery';
import './css/btn_style.css';
import HeartImg from './img/heart.png';

axios.defaults.withCredentials = true;
const headers = {withCredentials: true};

class Like extends Component {
    state={
        liked: 0
    }
    likePost=async()=>{
        $(".heart").on('click', ()=>{
            $(this).toggleClass("heart-blast");
        });
        const post_id=this.props.post;
        const send_param={post_id, headers};
        const result = await axios.post('http://localhost:9090/post/like', send_param);
        if(result.data.message){
            if(!this.state.liked){
                alert('좋아요!');
                this.setState({liked: 1});
                
                // this.props.ShowPost();
            }else{
                alert('좋아요 취소!');
                this.setState({liked: 0});
                
            }
        }else{
            alert('좋아요 오류');
        }
    }
    render(){
        return(
            <div >
                <Button variant="light" size="sm" onClick={this.likePost}><img src={HeartImg}  width='15' height='15' /></Button>
                <Button variant="light" size="sm" disabled={this.state.liked} value='좋아요'><Badge variant="light">{this.props.post}</Badge></Button>
            </div>
        );
    }
}

export default Like;
