import React, {Component} from 'react';
import {Button, Modal, useState} from 'react-bootstrap';
import axios from 'axios';

class SendMessage extends Component {
    Example=()=>{
        const [show, setShow] = useState(false);
      
        const handleClose = () => setShow(false);
        const handleShow = () => setShow(true);
      
        return (
          <>
            <Button variant="primary" onClick={handleShow}>
              Launch demo modal
            </Button>
      
            <Modal show={show} onHide={handleClose}>
              <Modal.Header closeButton>
                <Modal.Title>Modal heading</Modal.Title>
              </Modal.Header>
              <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
              <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                  Close
                </Button>
                <Button variant="primary" onClick={handleClose}>
                  Save Changes
                </Button>
              </Modal.Footer>
            </Modal>
          </>
        );
      }
        sendMessage= async()=>{
            // alert(this.props.user.nickname);
            const user_id=this.props.user.id;
            const send_param={user_id};
            try{
                const result= await axios.post('http://localhost:9090/message/send', send_param);
                if(result.data.message){
                    alert('쪽지보내기 성공');
                }else{
                    alert('쪽지보내기 실패');
                }
            }catch(err){
                console.log(err);
            }
        }
        render(){
            return(
                    <Button variant="info" size="sm" onClick={this.Example}>쪽지보내기</Button>
            );
        }
}

export default SendMessage;
