import React, {Component} from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import TableCell from '@material-ui/core/TableCell';
import $ from 'jquery';
import axios from 'axios';

axios.defaults.withCredentials = true;
const headers = {withCredentials: true};

class MyMessage extends Component {
    constructor(props){
        super(props);
        this.state={
            sent_msg: [],
            received_msg: [],
            update_form: "none",
            visible: "visible"
        };
    }
    componentDidMount(){
        this.ShowMessages();
    }
    ShowMessages=async ()=>{
        const user_id=$.cookie('login_id');
        console.log(user_id);
        const send_param={user_id, headers};
        if(user_id){
            const result=await axios.post('http://localhost:9090/message/allmessages', send_param);
        }else{
            alert('로그인이 필요합니다.');
        }
    }
    render(){
        const myPostList={
            visibility: this.state.visible
        }
        return(
            <div>
                <div>
                    <div>받은 쪽지함</div>
                    <div style={myPostList}>
                        <Table>
                        <TableHead>
                        <TableRow>
                        <TableCell>쪽지번호</TableCell>
                        <TableCell>발신인</TableCell>
                        <TableCell>생성일</TableCell>
                        <TableCell>수정일</TableCell>
                        <TableCell>내용</TableCell>
                        <TableCell></TableCell>
                        <TableCell></TableCell>
                        </TableRow>
                        </TableHead>
                        <TableBody>
                            {this.state.received_msg.map((message) => {
                            return <ReceivedMsg key={message.id} id={message.id} nickname={message.member.nickname} createDate={message.createdAt} updateDate={message.updatedAt} message={message.message} 
                                    superDelete={this.delete_post} superUpdate={this.update_post} entries={this.state.myposts} showPosts={this.ShowPosts}/>
                            })}
                            {this.checkMyPost}
                        </TableBody>
                        </Table>
                    </div>
                </div>
                <div>
                    <div>보낸 쪽지함</div>
                    <div style={myPostList}>
                        <Table>
                        <TableHead>
                        <TableRow>
                        <TableCell>쪽지번호</TableCell>
                        <TableCell>수신인</TableCell>
                        <TableCell>생성일</TableCell>
                        <TableCell>수정일</TableCell>
                        <TableCell>내용</TableCell>
                        <TableCell></TableCell>
                        <TableCell></TableCell>
                        </TableRow>
                        </TableHead>
                        <TableBody>
                            {/* {this.state.mymessages.map((message) => {
                            return <MyMessageItems key={message.id} id={message.id} nickname={message.member.nickname} createDate={message.createdAt} updateDate={message.updatedAt} message={message.message} 
                                    superDelete={this.delete_post} superUpdate={this.update_post} entries={this.state.myposts} showPosts={this.ShowPosts}/>
                            })}
                            {this.checkMyPost} */}
                        </TableBody>
                        </Table>
                    </div>
                </div>
            </div>
        );
    }
}

export default MyMessage;
