import React, {Component} from 'react';
import './css/main.css';
import $ from 'jquery';

class Join extends Component {
    inputFocus=()=>{
        const nick = this.nick_E.value;
        const email = this.email_E.value;
        const company = this.company_E.value;
        const career = this.career_E.value;

        const sendParam = {nick, email, company, career}
        $.post('http://localhost:8080/user/add', sendParam, function(returnData){
            if(returnData){
                alert(returnData.message);
            }
        });
    }
    render(){
        return(
            <div>
                <div class="textbox">
                <input placeholder="닉네임" type="hidden" id="nickname" ref={ref=>this.nick_E=ref}/><br/>
                <input placeholder="이메일" type="text" id="email" ref={ref=>this.email_E=ref}/><br/>
                <input placeholder="회사" type="text" id="company" ref={ref=>this.company_E=ref}/><br/>
                <input placeholder="직무" type="text" id="career" ref={ref=>this.career_E=ref}/><br/>
                <button>취소</button><button onClick={this.inputFocus}>등록</button>
                </div>
            </div>
        );
    }
}

export default Join;
