import React, {Component} from 'react';
import {Form, Button} from "react-bootstrap";
import axios from 'axios';
axios.defaults.withCredentials = true;
const headers = {withCredentials: true};

class Login extends Component {
    state={
        nick: ""
    }
    login=()=>{
        const send_param={
            headers,
            email: this.emailE.value,
            pwd: this.pwdE.value
        }
        axios.post('http://localhost:9090/member/login', send_param, function(returnData){
            const nickname = returnData.result.nickname;
            if(returnData.result){
                alert(nickname+ "님, 환영합니다");
            } else{
                alert("로그인 거부");
            }
        });
    }
    render(){
        return(
            <div>
                <h2>로그인</h2>
                <Form.Group controlId="formBasicEmail">
                    <Form.Control type="email" placeholder="Enter email" ref={ref=>this.emailE=ref} required/>
                </Form.Group>
                
                <Form.Group controlId="formBasicPassword">
                    <Form.Control type="password" placeholder="Enter Password" ref={ref=>this.pwdE=ref} required/>
                </Form.Group>

                <Button variant="primary" type="submit" onClick={this.login}>로그인</Button>
            </div>
        );
    }
}

export default Login;
