import React, { Component } from "react";
import axios from "axios";
import { Form, Badge, Col, Button, ButtonGroup } from "react-bootstrap";
import { Timeline, TimelineEvent } from "react-event-timeline";
import Moment from "react-moment";
import Comments from "../comment/comments";
import SendMessage from "../message/sendmessage";
import Like from "../user/like";

axios.defaults.withCredentials = true;
const headers = { withCredentials: true };

class PostItems extends Component {
  state = {
    message: ""
  };
  howUsername = username => {
    if (username) {
      return username;
    } else {
      return "탈퇴한 사용자";
    }
  };
  render() {
    const like_btn = {
      float: "right"
    };
    const nick_style = {
      float: "left"
    };
    let posts = this.props.entries.map(post => {
      return (
        <Col md={{ span: 6, offset: 3 }}>
          <Timeline>
            <TimelineEvent key={post.id}>
              <Form>
                <Form.Label as="textarea" rows="2" style={nick_style}>
                  <h6>
                    <strong>
                      {showUsername(post.member.nickname)}
                      {/* {post.member.nickname} */}
                    </strong>
                    &nbsp;
                    <SendMessage
                      user={post.member.nickname}
                      userId={post.member.id}
                      message={this.state.message}
                    />
                  </h6>
                </Form.Label>
                &nbsp; &nbsp; &nbsp; &nbsp; 생성일:{" "}
                <Moment format="YYYY-MM-DD HH:mm">{post.createdAt}</Moment>&nbsp; &nbsp; 수정일:{" "}
                <Moment format="YYYY-MM-DD HH:mm">{post.updatedAt}</Moment>
                <ButtonGroup style={like_btn}>
                  <Like
                    post={post.id}
                    user={post.member.id}
                    like={post.like}
                    show={this.props.show}
                  />
                </ButtonGroup>
                <hr />
                <h6> {post.content} </h6>
              </Form>
              <Form>
                <Comments postId={post.id} />
              </Form>
            </TimelineEvent>
          </Timeline>
        </Col>
      );

      {
        /* <div key={post.id} */
      } /* onClick={this.props.superDelete.bind(null, post.id)}>/*}
                    {/* <Card style={{ width: '18rem' }}>
                        <Card.Body>
                            <Card.Text>
                                {post.content} {post.id}
                            </Card.Text>
                            <Card.Link href="#">Card Link</Card.Link>
                            <Card.Link href="#">Another Link</Card.Link>
                        </Card.Body>
                        <Button onClick={this.props.superDelete.bind(null, post.id)}>삭제하기</Button> 
                        <Comments />
                    </Card>
                    <br />
                    </div> */
    });
    /*  }); */
    console.log(posts);

    return <ul className="thePost">{posts}</ul>;
  }
}

export default PostItems;
