import React, {Component} from 'react';
import TableRow from '@material-ui/core/TableRow';
import TableCell from '@material-ui/core/TableCell';
import {Button, InputGroup, FormControl} from 'react-bootstrap';

class MyPostItems extends Component {
    test2=()=>{
        this.test2.current.disabled=false;
        this.switchContent.current.disabled=true;
    }
    render(){
        return(
            <TableRow>
                <TableCell>{this.props.id}</TableCell>
                <TableCell>{this.props.nickname}</TableCell>
                <TableCell>{this.props.createDate}</TableCell>
                <TableCell>{this.props.updateDate}</TableCell>
                <TableCell ref={ref=>this.switchContent=ref}>{this.props.content}</TableCell>
                <InputGroup className="mb-3" ref={ref=>this.test2=ref}>
                    <FormControl as="textarea" aria-label="With textarea" />
                </InputGroup>
                <TableCell><Button variant="outline-warning" onClick={this.test}>수정</Button></TableCell>
                <TableCell><Button variant="outline-danger" onClick={this.props.superDelete.bind(null, this.props.id)}>삭제</Button></TableCell>
            </TableRow>
        );
    }
}

export default MyPostItems;
