import React, {Component} from 'react';
import axios from "axios";
import {Button, Form, Badge} from 'react-bootstrap';
import {Timeline, TimelineEvent} from 'react-event-timeline';
import Comments from './comments';

axios.defaults.withCredentials = true;
const headers = {withCredentials: true};

class PostItems extends Component {
    render(){
        let posts = this.props.entries.map((post)=>{
            return <Timeline>
                            <TimelineEvent key={post.id} createdAt={post.createdAt} >
                                <Form>
                                    <Form.Group>
                                        <Form.Label as="textarea" rows="2">
                                        {post.content} <Badge pill variant="primary">{post.memberId}</Badge>
                                        </Form.Label>
                                    </Form.Group> 
                                    <Button variant="warning" size="sm" onClick={this.props.superDelete.bind(null, post.id)}>삭제</Button>
                                    <Button variant="info" size="sm" onClick={this.props.superUpdate.bind(null, post.id)}>수정</Button>
                                </Form>
                                <Form>
                                    <Comments postId={post.id}/>
                                </Form>
                            </TimelineEvent>
                          
                    </Timeline>
            
                    {/* <div key={post.id} */} /* onClick={this.props.superDelete.bind(null, post.id)}>/*}
                    {/* <Card style={{ width: '18rem' }}>
                        <Card.Body>
                            <Card.Text>
                                {post.content} {post.id}
                            </Card.Text>
                            <Card.Link href="#">Card Link</Card.Link>
                            <Card.Link href="#">Another Link</Card.Link>
                        </Card.Body>
                        <Button onClick={this.props.superDelete.bind(null, post.id)}>삭제하기</Button> 
                        <Comments />
                    </Card>
                    <br />
                    </div> */});
       /*  }); */
        console.log(posts);

        return(
            <ul className="thePost">
                {posts}
            </ul>
        );
    }
}

export default PostItems;
