import React, {Component} from "react";
import {Form, Button} from "react-bootstrap";

class Timeline extends Component {
    post=()=>{
        const comment = this.commentE.value;
        alert(comment);
    }
    render(){
        return(
            <div>
                <div className="comment_form">
                    <h2>여러분의 소식을 들려주세요</h2>
                    <Form>
                        <Form.Group controlId="exampleForm.ControlTextarea1">
                            <Form.Control as="textarea" rows="3" placeholder="입력하세요" ref={ref=>this.commentE=ref}/>
                        </Form.Group>
                    <Button onClick={this.post}>글 등록</Button>
                    </Form>
                </div>
                <div id="comments"></div>
            </div>
        );
    }
}

export default Timeline;
